#hero {
    padding: 7.25em 2.5em 0 2.5em;

    // Title
    & h1 {
        font-size: 2rem;
        font-weight: bold;
        margin: 0;

        & .blinking {
            color: $green;
        }
    }

    // Icons
    & .social-links {
        margin: 0.4em 0 1.4em 0;
    }
    & .icons {
        color: $white;
        font-size: 1.4rem;
    }

    & a:hover {
        .icons {
            color: $pink;
            transition: 0.8s;
        }
    }

    // Terminal
    & #terminal {
        background-color: $fg-color;
        margin: 0.8em 0;
        height: 12.5em;
        border-radius: 10px;
        box-shadow: 5px 10px 8px rgba(0, 0, 0, 0.171);
        padding: 1.5625em 1.5625em;
        font-size: 1rem;
        & .Typewriter__cursor {
            color: $green;
        }
    }

    // Nav Buttons
    & .nav-btn {
        flex-direction: column;
        & button {
            color: inherit;
            border-radius: 8px;
            border: none;
            padding: 0.625em;
            font: inherit;
            cursor: pointer;
            outline: none;
            font-weight: bold;
        }
        & #work-btn {
            background-color: $dark-purple;
            margin: 1.25em 0;
        }
        & #contact-btn {
            background-color: $light-purple;
        }
    }

    // Tablet styles
    @include breakpoint-up(medium) {
        padding: 16em 14em 0 14em;
    }

    // Desktop styles
    @include breakpoint-up(large) {
        padding: 0;
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100vh;

        & h1 {
            font-size: 3.125rem;
        }

        & .icons {
            font-size: 1.875rem;
        }

        & .nav-btn {
            flex-direction: row;
            align-items: center;
            font-size: 1.25rem;
            & #work-btn {
                height: 3em;
                width: 10em;
                margin: 0;
            }
            & #contact-btn {
                height: 3em;
                margin-left: 1em;
                width: 10em;
            }
        }

        & #terminal {
            margin: 0 0 0 6em;
            width: 26.25em;
            height: 17.5em;
            font-size: 1.3rem;
            line-height: 1.5;
            padding: 2.2em;
        }
    }
}

#hero-page {
    height: 100vh;
}

