html {
    font-size: 100%;
    box-sizing: border-box;
    overflow-x: hidden;
}

*,
*::before,
*::after {
    box-sizing: inherit;
}

a {
    color: $white;
    text-decoration: none;
}

body {
    font-family: hack;
    line-height: 1.3;
    margin: 0px;
    padding: 0px;
    background: $bg-color;
    color: $white;
}

#particles-js {
    position: absolute;
    width: 100%;
    height: 100%;
    z-index: -1;
}

// Flexbox
.flex {
    display: flex;

    &-jc-sb {
        justify-content: space-between;
    }
    &-jc-c {
        justify-content: center;
    }
    &-ai-c {
        align-items: center;
    }
}

// Visibility
.hide-for-mobile {
    // hide for tablet and mobile devices
    @include breakpoint-down(medium) {
        display: none;
    }
}

.hide-for-desktop {
    // hide for desktop
    @include breakpoint-up(large) {
        display: none;
    }
}
// Font weight
.font {
    &-bold {
        font-weight: bold;
    }

    &-regular {
        font-weight: normal;
    }
}

.blinking {
    animation-name: blinking;
    animation-duration: 2s;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
}

@keyframes blinking {
    0% {
        opacity: 1;
    }
    50% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}

footer {
    margin-top: 5em;
    padding: 0.625em;
    & .f-text {
        margin: 0 auto;
        text-align: center;
        opacity: 0.6;
        text-shadow: 0 0 0;
        transition: opacity 0.3s ease, text-shadow 0.3s ease;
    }

    & .f-text:hover {
        opacity: 1;
        text-shadow: 0 0 1px;
    }
}

